package com.calmic.photovault.camera

import android.graphics.Bitmap
import android.graphics.Point
import org.opencv.android.Utils
import org.opencv.core.*
import org.opencv.imgproc.Imgproc
import kotlin.math.sqrt

class EdgeDetector {

    data class DetectedPhoto(
        val corners: List<Point>,
        val confidence: Float
    )

    fun detectPhotoEdges(bitmap: Bitmap): DetectedPhoto? {
        try {
            // Convert bitmap to OpenCV Mat
            val mat = Mat()
            Utils.bitmapToMat(bitmap, mat)

            // Resize for faster processing
            val resized = Mat()
            val scale = 600.0 / mat.width()
            Imgproc.resize(mat, resized, Size(), scale, scale, Imgproc.INTER_AREA)

            // Convert to grayscale
            val gray = Mat()
            Imgproc.cvtColor(resized, gray, Imgproc.COLOR_RGB2GRAY)

            // Apply Gaussian blur
            Imgproc.GaussianBlur(gray, gray, Size(5.0, 5.0), 0.0)

            // Edge detection using Canny
            val edges = Mat()
            Imgproc.Canny(gray, edges, 50.0, 150.0)

            // Dilate edges
            val kernel = Imgproc.getStructuringElement(Imgproc.MORPH_RECT, Size(3.0, 3.0))
            Imgproc.dilate(edges, edges, kernel)

            // Find contours
            val contours = ArrayList<MatOfPoint>()
            val hierarchy = Mat()
            Imgproc.findContours(edges, contours, hierarchy, Imgproc.RETR_EXTERNAL, Imgproc.CHAIN_APPROX_SIMPLE)

            // Find the largest contour
            if (contours.isEmpty()) return null

            var maxArea = 0.0
            var maxContour: MatOfPoint? = null

            for (contour in contours) {
                val area = Imgproc.contourArea(contour)
                if (area > maxArea) {
                    maxArea = area
                    maxContour = contour
                }
            }

            if (maxContour == null || maxArea < 1000) return null

            // Approximate polygon
            val contour2f = MatOfPoint2f(*maxContour.toArray())
            val approx = MatOfPoint2f()
            val peri = Imgproc.arcLength(contour2f, true)
            Imgproc.approxPolyDP(contour2f, approx, 0.02 * peri, true)

            // Convert to points
            val points = approx.toArray()

            // We expect 4 corners for a rectangle
            if (points.size == 4) {
                // Scale points back to original size
                val corners = points.map { p ->
                    Point(
                        (p.x / scale).toInt(),
                        (p.y / scale).toInt()
                    )
                }

                // Order corners: top-left, top-right, bottom-right, bottom-left
                val orderedCorners = orderCorners(corners)

                // Calculate confidence based on how rectangular it is
                val confidence = calculateRectangleConfidence(orderedCorners)

                // Clean up
                mat.release()
                resized.release()
                gray.release()
                edges.release()

                return DetectedPhoto(orderedCorners, confidence)
            }

            // Clean up
            mat.release()
            resized.release()
            gray.release()
            edges.release()

            return null

        } catch (e: Exception) {
            e.printStackTrace()
            return null
        }
    }

    private fun orderCorners(corners: List<Point>): List<Point> {
        // Order points: top-left, top-right, bottom-right, bottom-left
        val sorted = corners.sortedBy { it.x + it.y }

        val topLeft = sorted[0]
        val bottomRight = sorted[3]

        val remaining = sorted.subList(1, 3)
        val topRight: Point
        val bottomLeft: Point

        if (remaining[0].y < remaining[1].y) {
            topRight = remaining[0]
            bottomLeft = remaining[1]
        } else {
            topRight = remaining[1]
            bottomLeft = remaining[0]
        }

        return listOf(topLeft, topRight, bottomRight, bottomLeft)
    }

    private fun calculateRectangleConfidence(corners: List<Point>): Float {
        if (corners.size != 4) return 0f

        // Calculate side lengths
        val width1 = distance(corners[0], corners[1])
        val width2 = distance(corners[2], corners[3])
        val height1 = distance(corners[0], corners[3])
        val height2 = distance(corners[1], corners[2])

        // Calculate aspect ratio similarity
        val widthRatio = minOf(width1, width2) / maxOf(width1, width2)
        val heightRatio = minOf(height1, height2) / maxOf(height1, height2)

        // Average similarity
        return ((widthRatio + heightRatio) / 2).toFloat()
    }

    private fun distance(p1: Point, p2: Point): Double {
        val dx = (p1.x - p2.x).toDouble()
        val dy = (p1.y - p2.y).toDouble()
        return sqrt(dx * dx + dy * dy)
    }
}

package com.calmic.photovault.ui.camera

import android.content.Context
import android.graphics.*
import android.util.AttributeSet
import android.view.View

class EdgeOverlayView @JvmOverloads constructor(
    context: Context,
    attrs: AttributeSet? = null,
    defStyleAttr: Int = 0
) : View(context, attrs, defStyleAttr) {

    private val paint = Paint().apply {
        color = Color.GREEN
        strokeWidth = 4f
        style = Paint.Style.STROKE
        isAntiAlias = true
    }

    private val pointPaint = Paint().apply {
        color = Color.GREEN
        strokeWidth = 12f
        style = Paint.Style.FILL
        isAntiAlias = true
    }

    private var detectedCorners: List<Point>? = null

    fun drawDetectedEdges(corners: List<Point>) {
        detectedCorners = corners
        invalidate()
    }

    fun clearEdges() {
        detectedCorners = null
        invalidate()
    }

    override fun onDraw(canvas: Canvas) {
        super.onDraw(canvas)

        detectedCorners?.let { corners ->
            if (corners.size == 4) {
                // Draw lines connecting corners
                val path = Path()
                path.moveTo(corners[0].x.toFloat(), corners[0].y.toFloat())
                for (i in 1..3) {
                    path.lineTo(corners[i].x.toFloat(), corners[i].y.toFloat())
                }
                path.close()

                canvas.drawPath(path, paint)

                // Draw corner points
                corners.forEach { corner ->
                    canvas.drawCircle(
                        corner.x.toFloat(),
                        corner.y.toFloat(),
                        8f,
                        pointPaint
                    )
                }
            }
        }
    }
}

package com.calmic.photovault.ui.vault

import android.content.Intent
import android.os.Bundle
import android.view.MenuItem
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import com.calmic.photovault.PhotoVaultApplication
import com.calmic.photovault.data.model.FamilyVault
import com.calmic.photovault.databinding.ActivityVaultListBinding
import kotlinx.coroutines.launch

class VaultListActivity : AppCompatActivity() {

    private lateinit var binding: ActivityVaultListBinding
    private lateinit var app: PhotoVaultApplication
    private lateinit var adapter: VaultAdapter

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityVaultListBinding.inflate(layoutInflater)
        setContentView(binding.root)

        app = application as PhotoVaultApplication

        setupToolbar()
        setupRecyclerView()
        loadVaults()
        setupFab()
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        supportActionBar?.title = "Family Vaults"
    }

    private fun setupRecyclerView() {
        adapter = VaultAdapter { vault ->
            openVaultDetail(vault)
        }

        binding.recyclerView.apply {
            layoutManager = LinearLayoutManager(this@VaultListActivity)
            this.adapter = this@VaultListActivity.adapter
        }
    }

    private fun loadVaults() {
        binding.progressBar.visibility = android.view.View.VISIBLE

        lifecycleScope.launch {
            try {
                // Fetch vaults from server
                val result = app.apiService.getFamilyVaults()
                if (result.isSuccessful && result.body() != null) {
                    val vaults = result.body()!!.vaults
                    adapter.submitList(vaults)

                    // Save to local database
                    vaults.forEach { vault ->
                        app.database.familyVaultDao().insertVault(vault)
                    }
                } else {
                    // Load from local cache if server fails
                    app.database.familyVaultDao().getAllVaults().observe(this@VaultListActivity) { vaults ->
                        adapter.submitList(vaults)
                    }
                    Toast.makeText(this@VaultListActivity, "Using cached vaults", Toast.LENGTH_SHORT).show()
                }
            } catch (e: Exception) {
                // Load from local cache on error
                app.database.familyVaultDao().getAllVaults().observe(this@VaultListActivity) { vaults ->
                    adapter.submitList(vaults)
                }
                Toast.makeText(this@VaultListActivity, "Network error: ${e.message}", Toast.LENGTH_SHORT).show()
            } finally {
                binding.progressBar.visibility = android.view.View.GONE
            }
        }
    }

    private fun setupFab() {
        binding.fabCreateVault.setOnClickListener {
            showCreateVaultDialog()
        }
    }

    private fun showCreateVaultDialog() {
        val dialog = CreateVaultDialog { name, description ->
            createVault(name, description)
        }
        dialog.show(supportFragmentManager, "create_vault")
    }

    private fun createVault(name: String, description: String) {
        lifecycleScope.launch {
            try {
                val result = app.apiService.createVault(
                    com.calmic.photovault.network.model.CreateVaultRequest(name, description)
                )

                if (result.isSuccessful && result.body() != null) {
                    Toast.makeText(this@VaultListActivity, "Vault created successfully", Toast.LENGTH_SHORT).show()
                    loadVaults() // Refresh list
                } else {
                    Toast.makeText(this@VaultListActivity, "Failed to create vault", Toast.LENGTH_SHORT).show()
                }
            } catch (e: Exception) {
                Toast.makeText(this@VaultListActivity, "Error: ${e.message}", Toast.LENGTH_SHORT).show()
            }
        }
    }

    private fun openVaultDetail(vault: FamilyVault) {
        val intent = Intent(this, VaultDetailActivity::class.java).apply {
            putExtra("vault", vault)
        }
        startActivity(intent)
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            android.R.id.home -> {
                finish()
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }
}

package com.calmic.photovault.camera

import android.graphics.Bitmap
import android.graphics.Point
import com.calmic.photovault.data.model.EnhancementSettings
import org.opencv.android.Utils
import org.opencv.core.*
import org.opencv.imgproc.Imgproc
import org.opencv.photo.Photo

class ImageEnhancer {

    fun enhancePhoto(
        bitmap: Bitmap,
        settings: EnhancementSettings,
        corners: List<Point>? = null
    ): Bitmap {
        var mat = Mat()
        Utils.bitmapToMat(bitmap, mat)

        // Apply perspective correction if corners provided
        if (corners != null && corners.size == 4 && settings.perspectiveCorrection) {
            mat = applyPerspectiveCorrection(mat, corners)
        }

        // Auto color correction
        if (settings.autoCorrect) {
            mat = autoColorCorrect(mat)
        }

        // Denoise
        if (settings.denoise) {
            val denoised = Mat()
            Photo.fastNlMeansDenoisingColored(mat, denoised, 10f, 10f, 7, 21)
            mat.release()
            mat = denoised
        }

        // Adjust brightness and contrast
        if (settings.brightness != 1.0f || settings.contrast != 1.0f) {
            mat = adjustBrightnessContrast(mat, settings.brightness, settings.contrast)
        }

        // Adjust saturation
        if (settings.saturation != 1.0f) {
            mat = adjustSaturation(mat, settings.saturation)
        }

        // Sharpen
        if (settings.sharpen) {
            mat = sharpenImage(mat)
        }

        // Convert back to bitmap
        val result = Bitmap.createBitmap(mat.cols(), mat.rows(), Bitmap.Config.ARGB_8888)
        Utils.matToBitmap(mat, result)
        mat.release()

        return result
    }

    private fun applyPerspectiveCorrection(mat: Mat, corners: List<Point>): Mat {
        // Define source points (detected corners)
        val srcPoints = MatOfPoint2f()
        srcPoints.fromArray(
            *corners.map { org.opencv.core.Point(it.x.toDouble(), it.y.toDouble()) }.toTypedArray()
        )

        // Calculate target dimensions
        val width1 = distance(corners[0], corners[1])
        val width2 = distance(corners[2], corners[3])
        val maxWidth = maxOf(width1, width2).toInt()

        val height1 = distance(corners[0], corners[3])
        val height2 = distance(corners[1], corners[2])
        val maxHeight = maxOf(height1, height2).toInt()

        // Define destination points (rectangle)
        val dstPoints = MatOfPoint2f()
        dstPoints.fromArray(
            org.opencv.core.Point(0.0, 0.0),
            org.opencv.core.Point(maxWidth.toDouble(), 0.0),
            org.opencv.core.Point(maxWidth.toDouble(), maxHeight.toDouble()),
            org.opencv.core.Point(0.0, maxHeight.toDouble())
        )

        // Get perspective transformation matrix
        val transformMatrix = Imgproc.getPerspectiveTransform(srcPoints, dstPoints)

        // Apply perspective warp
        val warped = Mat()
        Imgproc.warpPerspective(
            mat, warped, transformMatrix,
            Size(maxWidth.toDouble(), maxHeight.toDouble())
        )

        mat.release()
        return warped
    }

    private fun autoColorCorrect(mat: Mat): Mat {
        // Convert to LAB color space
        val lab = Mat()
        Imgproc.cvtColor(mat, lab, Imgproc.COLOR_RGB2Lab)

        // Split channels
        val channels = ArrayList<Mat>()
        Core.split(lab, channels)

        // Apply CLAHE to L channel
        val clahe = Imgproc.createCLAHE(2.0, Size(8.0, 8.0))
        clahe.apply(channels[0], channels[0])

        // Merge channels
        Core.merge(channels, lab)

        // Convert back to RGB
        val result = Mat()
        Imgproc.cvtColor(lab, result, Imgproc.COLOR_Lab2RGB)

        lab.release()
        channels.forEach { it.release() }

        return result
    }

    private fun adjustBrightnessContrast(mat: Mat, brightness: Float, contrast: Float): Mat {
        val result = Mat()
        mat.convertTo(result, -1, contrast.toDouble(), (brightness - 1.0) * 255.0)
        return result
    }

    private fun adjustSaturation(mat: Mat, saturation: Float): Mat {
        val hsv = Mat()
        Imgproc.cvtColor(mat, hsv, Imgproc.COLOR_RGB2HSV)

        val channels = ArrayList<Mat>()
        Core.split(hsv, channels)

        // Adjust saturation channel
        channels[1].convertTo(channels[1], -1, saturation.toDouble(), 0.0)

        Core.merge(channels, hsv)

        val result = Mat()
        Imgproc.cvtColor(hsv, result, Imgproc.COLOR_HSV2RGB)

        hsv.release()
        channels.forEach { it.release() }

        return result
    }

    private fun sharpenImage(mat: Mat): Mat {
        val kernel = Mat(3, 3, CvType.CV_32F)
        kernel.put(0, 0,
            0.0, -1.0, 0.0,
            -1.0, 5.0, -1.0,
            0.0, -1.0, 0.0
        )

        val result = Mat()
        Imgproc.filter2D(mat, result, -1, kernel)

        kernel.release()
        return result
    }

    private fun distance(p1: Point, p2: Point): Double {
        val dx = (p1.x - p2.x).toDouble()
        val dy = (p1.y - p2.y).toDouble()
        return kotlin.math.sqrt(dx * dx + dy * dy)
    }
}

package com.calmic.photovault.network

import android.content.Context
import com.calmic.photovault.BuildConfig
import com.calmic.photovault.util.PreferenceManager
import okhttp3.Interceptor
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory
import java.util.concurrent.TimeUnit

object RetrofitClient {

    private var retrofit: Retrofit? = null
    private var context: Context? = null

    fun getInstance(appContext: Context): ApiService {
        context = appContext

        if (retrofit == null) {
            val loggingInterceptor = HttpLoggingInterceptor().apply {
                level = if (BuildConfig.DEBUG) {
                    HttpLoggingInterceptor.Level.BODY
                } else {
                    HttpLoggingInterceptor.Level.NONE
                }
            }

            val authInterceptor = Interceptor { chain ->
                val token = PreferenceManager.getAuthToken(appContext)
                val request = if (token != null) {
                    chain.request().newBuilder()
                        .addHeader("Authorization", "Bearer $token")
                        .build()
                } else {
                    chain.request()
                }
                chain.proceed(request)
            }

            val client = OkHttpClient.Builder()
                .addInterceptor(loggingInterceptor)
                .addInterceptor(authInterceptor)
                .connectTimeout(30, TimeUnit.SECONDS)
                .readTimeout(30, TimeUnit.SECONDS)
                .writeTimeout(30, TimeUnit.SECONDS)
                .build()

            retrofit = Retrofit.Builder()
                .baseUrl(BuildConfig.API_BASE_URL)
                .client(client)
                .addConverterFactory(GsonConverterFactory.create())
                .build()
        }

        return retrofit!!.create(ApiService::class.java)
    }

    fun clearInstance() {
        retrofit = null
    }
}

